import React from 'react';
//import App from './App';
//import { ReactDOM } from 'react'; 


function greeting() {
  
  const date = new Date();
  const currentTime = date.getHours();

  const customStyle = {
    color: ' '
  };

  if (currentTime > 12) {
    greeting = 'afternoon';
    customStyle.color = 'red';
  } else if (currentTime < 12) {
    greeting = 'morning';
    customStyle.color = 'green';
  }


 
return <h1 className="greeting "style={customStyle}> {greeting} </h1>
}

export default greeting;
